import { NextResponse } from "next/server";
import nodemailer from "nodemailer";
export const HeaderImage = ``;

export async function GET() {
  const products = [
    { id: 1, name: "Product A", price: 100 },
    { id: 2, name: "Product B", price: 200 },
  ];
  return NextResponse.json(products);
}

export async function POST(req) {
  const body = await req.json();
  if (req.method === "POST") {
    const { name, email, fileNumber } = body;

    // Create a Nodemailer transporter
    const transporter = nodemailer.createTransport({
      service: "gmail",
      auth: {
        user: process.env.EMAIL_HOST_USER,
        pass: process.env.EMAIL_HOST_PASSWORD,
      },
    });

    // Remove the data:image/jpeg;base64, prefix if it exists
    const headerImageBase64 = HeaderImage.replace(
      /^data:image\/[a-z]+;base64,/,
      ""
    );

    const emailHTML = `
    <div style="max-width: 600px; margin: auto; font-family: Arial, sans-serif; border: 1px solid #ddd; padding: 20px;">
      <!-- Header Image -->
      <img src="cid:headerImage" alt="logo page" style="width: 100%;" />
      <p>Dear customer, ${name}</p>
      <p>Your request has been received successfully</p>
      
      <p><strong>Application type:</strong> Asylum file to Canada</p>
      <p>The request is currently being processed.</p>
      <p>The file will be prepared and submitted to the Canadian Department of Immigration, and an application for asylum and resettlement will be submitted.</p>
      <p>We will provide you with all new updates later</p>
      <h2 style="color: #d63031; text-align: center;">File Processing Number</h2>
      <h1 style="color: #d63031; text-align: center;">7483927</h1>
      <p>In order to continue working properly,</p>
      <p>Please provide us with the necessary power of attorney.</p>
      <p>By signing the document below in the space provided for you</p>
      <p>And send it to us as a scanned PDF file.</p>
      <!-- Footer -->
      <div style="background: #1e3a8a; padding: 10px; text-align: center; color: white; margin-top: 20px;">
        <p><a href="https://dordicklaw.info" style="color: #f1c40f; text-decoration: none;">Our website</a></p>
      </div>
      <!-- Contact Info -->
      <div style="margin-top: 10px; padding: 10px; background: #f1f1f1; border-radius: 5px; text-align: center;">
        <p><strong>Maria Wilson</strong></p>
        <p>📧 info@dordicklaw.info</p>
        <p>📞 8005555595</p>
        <p>📍 Wood St, Whitehorse, YT Y1A 0A1, Canada</p>
        <p>🔗 <a href="https://dordicklaw.info" style="color: blue;">https://dordicklaw.info</a></p>
      </div>
    </div>
    `;

    // Email content with attachments
    const mailOptions = {
      from: process.env.EMAIL_HOST_USER,
      to: "jy_attou@esi.dz",
      subject: "New Form Submission",
      html: emailHTML,
      attachments: [
        {
          filename: "header.jpg",
          content: headerImageBase64,
          encoding: "base64",
          cid: "headerImage", // Same as the image src in the HTML
        },
      ],
    };

    try {
      console.log("Sending email...");
      await transporter.sendMail(mailOptions);
      console.log("Email sent successfully");
      return NextResponse.json(
        { message: "Email sent successfully!", data: body },
        { status: 201 }
      );
    } catch (error) {
      console.error("Error sending email:", error);
      return NextResponse.json(
        { message: "An error occurred while sending the email." },
        { status: 500 }
      );
    }
  } else {
    return NextResponse.json(
      { message: "Invalid request method." },
      { status: 405 }
    );
  }
}
